if option == "Write your own biography":
    input_text = st.text_area(label='Enter your biography')

else:
    input_text = st.selectbox("Select a ready-made biography:", biographies)

# Display the selected or written biography
if st.button(label='Submit'):
    processed_text = preprocess_text(input_text)
    pca_result = feature_extraction(processed_text)
    cluster_num, cluster_name = predict(pca_result)
    st.write(f"This biography belongs to **Cluster {cluster_num}: {cluster_name}**.")